mapper.select:
	
		  select g_no
		    from board a
           where a.g_no = (select ifnull(MAX(g_no),0) from board);

mapper.update:

	update board
	   set o_no = o_no + 1
	 where g_no=#{gNo } and o_no >= #{oNo}


	update board
	   set title = #{title}, contents = #{contents}
	 where no = #{no}


	update board
	   set hit = hit + 1
	 where no=#{no }
